mapper.select:
select
id, owner_id, name,
start_time, end_time,
cost, description,
create_time, create_by,
edit_time, edit_by
from t_activity


        and owner_id = #{ownerId}


        and 'name' like "%"#{name}"%"


        and create_time = #{createTime}


        and start_time between #{startTime} and #{endTime}
        and end_time between #{startTime} and #{endTime}


        and cost >= #{cost}
