mapper.select:
select
    id, owner_id, name,
    start_time, end_time,
    cost, description,
    create_time, create_by,
    edit_time, edit_by
from t_activity


        and owner_id = #{ownerId}


        and 'name' like "%"#{name}"%"


        and create_time >= #{createTime}


        and start_time between #{startTime} and #{endTime}
        and end_time between #{startTime} and #{endTime}


        and cost >= #{cost}


        and ${filterSQL}

mapper.update:
update t_activity
owner_id = #{ownerId}, 'name' = #{name}, start_time = #{startTime}, end_time = #{endTime}, cost = #{cost}, 'description' = #{description}, create_time = #{createTime}, create_by = #{createBy}, edit_time = #{editTime}, edit_by = #{editBy}
where id = #{id}
