mapper.select:
select id, clue_id, note_way, note_content, create_time, create_by, edit_time, edit_by, deleted from t_clue_remark
and ${filterSQL}
and (deleted = 0 or deleted is null)

select id, clue_id, note_way, note_content, create_time, create_by, edit_time, edit_by, deleted from t_clue_remark
and ${filterSQL}
and (deleted = 0 or deleted is null)
and clue_id = #{clueId}

mapper.update:
update t_clue_remark
clue_id = #{clueId}, note_way = #{noteWay}, note_content = #{noteContent}, create_time = #{createTime}, create_by = #{createBy}, edit_time = #{editTime}, edit_by = #{editBy}, deleted = #{deleted},
where id = #{id}
